"use strict";

export default function(actions, store) {
  let wrapper = Object.create({}, {
    store: {
      get: () => store
    }
  });

  actions.constructor.apiMethods.forEach(method => {
    Object.defineProperty(wrapper, method, {
      value: (...args) => {
        return actions[method].call(actions, ...args);
      }
    });
  });

  return wrapper;
}

"use strict";

export default {
  toQueryString(query) {
    if (!query) {
      return "";
    }

    return Object.keys(query).map(key => `${key}=${encodeURIComponent(query[key])}`).join("&");
  }
};

"use strict";

import Constants    from "../Constants";
import SessionStore from "../stores/Sessions";

function _performRequest(method, url, options) {
  options = (options || {});
  options.method = method;
  options.headers = (options.headers || {});
  options.headers[Constants.OAUTH_HEADER] = SessionStore.getAccessToken();

  return fetch(`https://${SessionStore.getDomain()}${url}`, options).then(response => {
    return response.json();
  });
}

class WebAPI {
  get(url, options) {
    return _performRequest("get", url, options);
  }
}

/** @ignore */
